    const [Text,setText]=useState("Click Me");
    const fun1=()=>{
        if(Text==='Click Me'){
            setText("..Aree");
            setColor("orange");
        }   
        else if(Text=="..Aree"){
            setColor('lightyellow');
            setText('Mad');
        }
        else{
            
        }
    }
    return (
        <>
            <div className="CLICK" style={{backgroundColor:color}}>
            {/* <h1>{count}</h1> */}
            <button onClick={fun1}>{Text}</button>
            </div>
        </>
    )
}
export default App;
