import React, { useState } from "react";
const App=()=>{
    const [fullName,setFullName]=useState({
        fname:"",
        lname:"",
        email:"",
        phone:""
    })
    const fun1=(e)=>{
        const name=e.target.name;
        const val=e.target.value;
        console.log(name);
        console.log(val);
        setFullName((prevVal)=>{
             if(name==='fName'){
                return{
                fname:val,
                lname:prevVal.lname
                }
             }else{
                return{
                    fname:prevVal.fname,
                    lname:val
                }
             }
        })
    }
    return (
        <>
            <div className="CLICK">
            <h1>Hello {fullName.fname} {fullName.lname}</h1>
                <input type="text" placeholder={"Enter Your Name"} 
                 name='fName'
                 value={fullName.fname}
                 onChange={fun1}
                 />
                <input type="text" 
                placeholder={"Enter Your Last Name"} 
                name='lName'
                value={fullName.lname}
                onChange={fun1}
                />
                <input type={"email"}
                 name="email"
                 value={fullName.email}
                 onChangege={fun1}
                 placeholder="Enter Your "
                />
                <input type={"text"}
                 name="email"
                 value={fullName.email}
                 onChangege={fun1}
                />
                <button type="submit" 
                // onClick={fun}
                 >Submit</button>
            </div>
        </>
    )
}
export default App;
