import React, { useState } from 'react'

const App=()=>{
  let flag=0;
  const [Increment,setIncrement]=useState({
     inc:0,
     dec:0,
     fi:0
  });
  const fun=(e)=>{
       const name=e.target.name;
       setIncrement(()=>{
         if(flag &Increment.fi<=0){
           return alert("Negative Not Allowed");
         } 
         else if(name==='Inc'){
            return{
              inc:Increment.inc+1,
              dec:Increment.inc,
              fi:Increment.inc
            }
          }
          else{
            return{
              dec:Increment.dec-1,
              inc:Increment.dec,
              fi:Increment.dec
            }
          }
       });
  } 
  return(
        <>
        <div className="Main_div">
          <div className="INC_DEC">
           <h1>{Increment.fi}</h1>
           <div className='btn'>
          <button onClick={fun} name="Inc">Increment</button>
          <button onClick={fun} name="Dec">Decrement</button>
          </div>
          </div>
        </div>
        </>
    )
}








export default App;
