import React, { useState } from 'react'

const App=()=>{
  const [Increment,setIncrement]=useState({
     inc:0,
     dec:0
  });
  const fun=(e)=>{
       const name=e.target.v;
       setIncrement((ele)=>{
          if(name==='Inc'){
            return{
              inc:Increment.inc+1,
              dec:ele.dec
            }
          }
          else{
            return{
              inc:ele.inc,
              dec:Increment.dec-1
            }
          }
       });
  } 
  return(
        <>
        <div className="Main_div">
          <div className="INC_DEC">
           <h1>{Increment.inc}</h1>
           <div className='btn'>
          <button onClick={fun} name="Inc">Increment</button>
          <button onClick={fun} name="Dec">Decrement</button>
          </div>
          </div>
        </div>
        </>
    )
}








export default App;
