// import React, { useEffect, useState } from "react";
import axios from "axios";
const  Compo=()=>{
    let [Val,setVal]=useState(1);
    const [name,SetName]=useState();
    const [move,Setmove]=useState();
    const [movename,setmovename]=useState();
    const fun=(e)=>{
        setVal(e.target.value);
    } 
    useEffect(()=>{
        async function getData(){
            const res=await axios.get(`https://pokeapi.co/api/v2/pokemon/${Val}`);
            console.log(res.data);
            SetName(res.data.name);
            Setmove(res.data.moves.length);
            setmovename(res.data.moves[0].move.name);
        }
        getData();
        // alert("hello")
    });
    return (
    <>
    <h1>You Choose <span style={{color:"red"}}>{Val}</span ></h1>
    <h1>My Name is <span style={{color:"red"}}>{name}</span></h1>
    <h1>You Choose <span style={{color:"red"}}>{move}</span></h1>
    <h1>It's <span style={{color:"red"}}>{movename}</span> move</h1>
    <select value={Val} onClick={fun}>
        <option value="1">1</option>
        <option value="25">25</option>
        <option value="3">3</option>
        <option value="4">4</option>
        <option value="5">5</option>
    </select>
    </>
    )
}
export default Compo;
